INSERT INTO `products` (`id`, `name`, `category_id`, `brand_id`, `detail`, `unit_price`, `promotion_price`, `quantity`, `created_at`, `updated_at`) VALUES
(1, 'iPhone 7 Plus 32GB', 1, 1, '<p>Ra mắt v&agrave;o năm 2016, iPhone 7 Plus l&agrave; một trong những d&ograve;ng flagship ấn tượng của Apple. T&iacute;nh đến hiện tại, iPhone 7 Plus 32GB ch&iacute;nh h&atilde;ng VN/A vẫn l&agrave; một sự lựa chọn đ&aacute;ng gi&aacute; cho người d&ugrave;ng đang t&igrave;m kiếm một sản phẩm chất lượng, đẳng cấp v&agrave; c&oacute; mức gi&aacute; b&aacute;n ph&ugrave; hợp<br />\r\niPhone 7 Plus 32GB c&oacute; k&iacute;ch thước 158.2 x 77.9 x 7.3 (mm) được Apple giữ lại gần như nguy&ecirc;n vẹn ng&ocirc;n ngữ thiết kế từ bộ đ&ocirc;i iPhone 6s/6s Plus với vỏ kim loại nguy&ecirc;n khối tinh tế, mỏng nhẹ nhưng cũng rất chắc chắn. Tuy nhi&ecirc;n, phần dải an-ten bắt s&oacute;ng thay v&igrave; được đặt ngang mặt lưng như người tiền nhiệm th&igrave; nay đ&atilde; được chuyển sang phần cạnh tr&ecirc;n của sản phẩm.</p>', 12500000, 11400000, 20, '2019-12-16 09:47:22', '2020-01-10 16:50:07'),
(2, 'iPhone 7 Plus 128GB', 1, 1, '<h2><strong><a href=\"https://cellphones.com.vn/iphone-7-plus-128-gb-cty-vn.html\" target=\"_blank\">iPhone 7 Plus 128Gb</a></strong></h2>\r\n\r\n<p><em><strong>iPhone 7 Plus 128GB v</strong>ới thiết kế kh&ocirc;ng qu&aacute; nhiều thay đổi, vẫn bảo tồn vẻ đẹp truyền thống từ thời&nbsp;iPhone 6 Plus. Tuy nhi&ecirc;n, phần cứng&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple/iphone-7-7-plus.html\" target=\"_blank\">iPhone 7 Plus</a>&nbsp;đ&atilde; được n&acirc;ng cấp đ&aacute;ng kể như camera k&eacute;p&nbsp;c&ugrave;ng cấu h&igrave;nh cực mạnh.</em></p>', 14500000, 13500000, 37, '2019-12-16 09:50:24', '2019-12-16 09:50:24'),
(3, 'iPhone 7 32GB', 1, 1, '<h2><strong>Đặc điểm nổi bật của&nbsp;<a href=\"https://cellphones.com.vn/apple-iphone-7-32-gb-chinh-hang-vn.html\" target=\"_blank\">iPhone 7 32GB</a></strong></h2>\r\n\r\n<p><em><a href=\"https://cellphones.com.vn/mobile/apple.html\" target=\"_blank\">iPhone</a>&nbsp;lu&ocirc;n l&agrave; một d&ograve;ng điện thoại được ưa chuộng nhất bởi sự ổn định với hệ điều h&agrave;nh iOS, sự mạnh mẽ với con chip Apple&nbsp; A series, v&agrave; d&ograve;ng&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple/iphone-7-7-plus.html\" target=\"_blank\">iPhone 7</a>&nbsp;cũng kh&ocirc;ng ngoại lệ. iPhone 7 32GB ch&iacute;nh h&atilde;ng VN/A l&agrave; một chiếc điện thoại chất lượng tốt xứng đ&aacute;ng l&agrave; một si&ecirc;u phẩm đ&aacute;ng mua hiện nay.</em></p>', 9900000, 8400000, 42, '2019-12-16 09:52:31', '2019-12-16 09:52:31'),
(4, 'iPhone X 64GB', 1, 1, '<h2><strong>iPhone X 64GB</strong></h2>\r\n\r\n<p><em>Sản phẩm kỷ niệm 10 năm ra mắt của Apple n&ecirc;n&nbsp;<strong>iPhone X 64GB&nbsp;</strong>c&oacute; sự thay đổi so với bộ đ&ocirc;i iPhone 8 - 8 Plus&nbsp;trước đ&oacute; từ t&iacute;nh năng đến thiết kế khiến nhiều người d&ugrave;ng smartphone khao kh&aacute;t.</em></p>', 19900000, 18600000, 23, '2019-12-16 09:55:20', '2019-12-16 09:55:20'),
(5, 'iPhone X 256GB', 1, 1, '<h2><strong><a href=\"https://cellphones.com.vn/apple-iphone-x-256-gb-chinh-hang-vn.html\" target=\"_blank\">iPhone X 256GB</a>&nbsp;gi&aacute; tốt, nhiều khuyến m&atilde;i</strong></h2>\r\n\r\n<p><em>Nếu bạn đang cần một thiết bị&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple.html\" target=\"_blank\"><strong>iPhone</strong></a>&nbsp;với cấu h&igrave;nh rất tốt, lưu trữ thoải m&aacute;i, được trang bị hầu hết c&aacute;c t&iacute;nh năng, v&agrave; đặc biệt l&agrave; với một mức gi&aacute; hợp l&yacute; th&igrave;&nbsp;<strong>iPhone X 256GB ch&iacute;nh h&atilde;ng VN/A</strong>&nbsp;ch&iacute;nh l&agrave; sự lựa chọn d&agrave;nh cho bạn.&nbsp;</em></p>', 25000000, 23000000, 22, '2019-12-16 09:58:09', '2019-12-16 09:58:09'),
(9, 'iPhone 11 256GB', 1, 1, '<h2><strong><a href=\"https://cellphones.com.vn/iphone-11.html\" target=\"_blank\">iPhone 11</a>&nbsp;ch&iacute;nh h&atilde;ng VN/A&nbsp;&ndash;&nbsp;</strong><strong>Chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">điện thoại</a>&nbsp;nhiều m&agrave;u sắc, camera n&acirc;ng cấp</strong></h2>\r\n\r\n<p><em><strong>iPhone 11</strong>&nbsp;l&agrave; mẫu iPhone c&oacute; nhiều m&agrave;u sắc nhất của Apple được ra mắt trong năm 2019. B&ecirc;n cạnh đ&oacute;, cấu h&igrave;nh iPhone 11 c&ugrave;ng với&nbsp;<a href=\"https://cellphones.com.vn/iphone-11-pro.html\" target=\"_blank\">iPhone 11 Pro</a>,&nbsp;<a href=\"https://cellphones.com.vn/iphone-11-pro-max.html\" target=\"_blank\">iPhone 11 Pro Max</a>&nbsp;cũng được n&acirc;ng cấp đặc biệt về cụm camera sau v&agrave; Faceid, vi&ecirc;n pin dung lượng lớn hơn.</em></p>', 25990000, 24700000, 20, '2019-12-16 10:03:48', '2019-12-16 10:03:48'),
(10, 'iPhone 11 64GB', 1, 1, '<p><strong>Apple iPhone 11</strong>&nbsp;với 6 phi&ecirc;n bản m&agrave;u sắc. Camera c&oacute; khả năng chụp ảnh vượt trội, thời lượng pin cực d&agrave;i v&agrave; bộ vi xử l&yacute; mạnh nhất từ trước đến nay. Mang đến trải nghiệm tuyệt vời d&agrave;nh cho bạn.</p>', 21900000, 20600000, 20, '2019-12-16 10:05:16', '2019-12-16 10:05:16'),
(11, 'iPhone 11 Pro Max 64GB', 1, 1, '<p><strong>Apple iPhone 11 Pro MAX 64GB Ch&iacute;nh h&atilde;ng</strong>&nbsp;sở hữu hệ thống 3 camera đẳng cấp. Một bước nhảy vọt về thời lượng pin v&agrave; con chip mới c&oacute; hiệu năng mạnh mẽ chưa từng thấy. Mạnh mẽ, kh&aacute;c biệt v&agrave; chuy&ecirc;n nghiệp,&nbsp;<strong>iPhone 11 Pro Max</strong>&nbsp;ho&agrave;n to&agrave;n xứng đ&aacute;ng với t&ecirc;n gọi.</p>', 33990000, 31200000, 24, '2019-12-16 10:06:16', '2019-12-16 10:06:16'),
(12, 'iPhone 11 Pro Max 256GB', 1, 1, '<h2><strong><a href=\"https://cellphones.com.vn/iphone-11-pro-max.html\" target=\"_blank\">iPhone 11 Pro Max</a>&nbsp;ch&iacute;nh h&atilde;ng Vn/a&nbsp;&ndash; Trải nghiệm chụp ảnh đỉnh cao&nbsp;</strong><strong>với 3 camera sau</strong></h2>\r\n\r\n<p><em><strong>iPhone 11 Pro Max</strong>&nbsp;l&agrave; mẫu smartphone cao cấp nhất của Apple được ra mắt năm 2019. Với thiết kế cao cấp, hệ thống camera 3 camera c&ugrave;ng cấu h&igrave;nh si&ecirc;u mạnh mẽ th&igrave; iPhone 11 Pro Max ch&iacute;nh l&agrave; một chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">smartphone&nbsp;</a>đ&aacute;p ứng mọi trải nghiệm của người d&ugrave;ng.</em></p>', 37990000, 34390000, 16, '2019-12-17 07:35:40', '2019-12-17 07:35:40'),
(13, 'Samsung Galaxy Note 10+ (Plus)', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-plus.html\" target=\"_blank\">Samsung Galaxy Note 10 Plus</a>&nbsp;&ndash; M&agrave;n h&igrave;nh lớn cho trải nghiệm tuyệt đỉnh, S-Pen tiện dụng</strong></h2>\r\n\r\n<p><em>L&agrave; phi&ecirc;n bản n&acirc;ng cấp đ&aacute;ng gi&aacute; ra đời c&ugrave;ng thời điểm với&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-chinh-hang.html\" target=\"_blank\">Samsung Galaxy Note 10</a>,&nbsp;<strong>Samsung Galaxy Note 10 Plus&nbsp;</strong>l&agrave; sự lựa chọn tuyệt vời cho người d&ugrave;ng đam m&ecirc; c&ocirc;ng nghệ c&oacute; nhu cầu sở hữu một chiếc điện thoại ho&agrave;n hảo về mọi mặt. Với Note 10 Plus,&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a><strong>&nbsp;</strong>đ&atilde; thật sự mang đến một chiếc điện thoại c&oacute; sức mạnh vượt trội, đ&aacute;p ứng to&agrave;n diện c&aacute;c nhu cầu về l&agrave;m việc v&agrave; giải tr&iacute; của người d&ugrave;ng. Ngo&agrave;i ra, bạn c&oacute; thể tham khảo phi&ecirc;n bản r&uacute;t gọn&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-lite.html\" target=\"_blank\">Note 10 Lite</a>&nbsp;với gi&aacute; b&aacute;n phải chăng hơn.</em></p>', 26990000, 24990000, 32, '2019-12-29 01:07:16', '2019-12-29 01:07:16'),
(14, 'Samsung Galaxy Note 10', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-chinh-hang.html\" target=\"_blank\">Điện thoại Samsung Galaxy Note 10</a>&nbsp;&ndash; Trải nghiệm m&aacute;y t&iacute;nh trong ch&iacute;nh&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_self\">smartphone&nbsp;</a>của bạn</strong></h2>\r\n\r\n<p>Với h&agrave;ng loạt những si&ecirc;u phẩm từng tung ra thị trường,&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a>&nbsp;lại một lần nữa l&agrave;m cộng đồng người y&ecirc;u c&ocirc;ng nghệ phải đứng ngồi kh&ocirc;ng y&ecirc;n khi tr&igrave;nh l&agrave;ng bộ đ&ocirc;i&nbsp;<strong>Samsung Galaxy Note 10</strong>&nbsp;v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-plus.html\" target=\"_blank\"><strong>Samsung Galaxy Note 10 Plus</strong></a>&nbsp;mang đến những trải nghiệm tuyệt đỉnh như m&aacute;y t&iacute;nh ngay từ ch&iacute;nh chiếc smartphone của bạn, gi&uacute;p bạn c&oacute; thể l&agrave;m mọi thứ chỉ bằng một chiếc điện thoại. Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo phi&ecirc;n bản r&uacute;t gọn với gi&aacute; b&aacute;n phải chăng hơn l&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-lite.html\" target=\"_blank\">Samsung Note 10 Lite</a>.</p>', 22990000, 20990000, 10, '2019-12-29 01:08:38', '2019-12-29 01:08:38'),
(15, 'Samsung Galaxy S10', 1, 2, '<h2><a href=\"https://cellphones.com.vn/samsung-galaxy-s10-chinh-hang.html\" target=\"_blank\">Điện thoại Samsung Galaxy S10</a>&nbsp;- Cập nhật th&ocirc;ng tin cấu h&igrave;nh, gi&aacute; b&aacute;n, chương tr&igrave;nh khuyến m&atilde;i</h2>\r\n\r\n<p><em>Sự kiện Samsung Unpacked 2019 đ&atilde; kh&eacute;p lại với th&agrave;nh c&ocirc;ng kh&ocirc;ng thể tuyệt vời hơn, ch&iacute;nh l&agrave; biết thế hệ S thứ 10 của m&igrave;nh th&agrave;nh t&acirc;m điểm, chiếm s&oacute;ng to&agrave;n bộ spotlight của Đại hội MWC 2019. Thực sự, Samsung Galaxy S10 kh&ocirc;ng hẳn l&agrave; bản n&acirc;ng cấp ho&agrave;n hảo về mọi mặt nhưng n&oacute; hội tụ đủ, đ&uacute;ng những yếu tố cần thiết để người d&ugrave;ng cả thấy kh&ocirc;ng nh&agrave;m ch&aacute;n v&agrave; h&agrave;o hứng muốn sở hữu. Samsung Galaxy S10 cũng ch&iacute;nh l&agrave; bước s&atilde;i ch&acirc;n xa bắt kịp với c&aacute;c đối thủ song vẫn chứng minh ng&ocirc;i vương của bản th&acirc;n sau một năm nhạt nh&ograve;a.</em>&nbsp;<em>Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo th&ecirc;m phi&ecirc;n bản r&uacute;t gọn, gi&aacute; rẻ hơn đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s10-lite.html\" target=\"_blank\">Galaxy S10 Lite</a>.</em></p>', 19990000, 19390000, 20, '2019-12-29 01:10:31', '2019-12-29 01:10:31'),
(16, 'Samsung Galaxy S10+ (Plus)', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10plus-chinh-hang.html\" target=\"_blank\">Samsung Galaxy S10 Plus</a>&nbsp;ch&iacute;nh h&atilde;ng, nhiều ưu đ&atilde;i khi đặt trước</strong></h2>\r\n\r\n<p><em>Vậy l&agrave;&nbsp;<strong><a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-s.html\" target=\"_blank\">Samsung Galaxy S&nbsp;</a></strong>thế hệ thứ 10 đ&atilde; được ra mắt, v&agrave; kh&ocirc;ng l&agrave;m người d&ugrave;ng thất vọng một ch&uacute;t n&agrave;o. Trong bộ ba si&ecirc;u phẩm d&ograve;ng S năm nay,&nbsp;<strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10plus-chinh-hang.html\" target=\"_blank\">Samsung Galaxy S10 Plus</a>&nbsp;</strong>l&agrave; thiết bị đắt gi&aacute; cũng như được trang bị những t&iacute;nh năng đ&igrave;nh đ&aacute;m nhất từ&nbsp;<strong><a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a></strong>. Đ&acirc;y hứa hẹn sẽ trở th&agrave;nh một trong những smartphone ho&agrave;n hảo nhất năm 2019. Ngo&agrave;i ra, c&aacute;c si&ecirc;u phẩm dự kiến tr&igrave;nh l&agrave;ng của Samsung như&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s10e.html\" target=\"_blank\"><strong>Samsung Galaxy S10e</strong></a>,&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note.html\" target=\"_blank\"><strong>Samsung Galaxy Note 10&nbsp;</strong></a>với những thiết kế c&ugrave;ng t&iacute;nh năng đột ph&aacute; hứa hẹn sẽ b&ugrave;ng nổ giới c&ocirc;ng nghệ trong năm nay. Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo phi&ecirc;n bản r&uacute;t gọn&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s10-lite.html\" target=\"_blank\">Samsung S10 Lite</a>&nbsp;với gi&aacute; b&aacute;n phải chăng hơn.</em></p>', 22990000, 20190000, 23, '2019-12-29 01:12:00', '2019-12-29 01:12:00'),
(17, 'Samsung Galaxy S10+ (Plus) 512GB', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10-plus-512gb.html\" target=\"_blank\">Samsung Galaxy S10 Plus 512GB&nbsp;</a></strong></h2>\r\n\r\n<p><em>C&ugrave;ng với Galaxy S10, Galaxy S10 Plus sẽ đ&aacute;nh dấu 10 năm d&ograve;ng Galaxy S ra đời. Ch&iacute;nh v&igrave; vậy, sản phẩm n&agrave;y hứa hẹn sẽ mang đến nhiều cải tiến gi&aacute; trị so với những người tiền nhiệm v&agrave; tạo ra c&uacute; đột ph&aacute; mới cho&nbsp;<strong><a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a></strong>, nhất l&agrave; trong ho&agrave;n cảnh thị trường smartphone đang c&oacute; dấu hiệu b&atilde;o h&ograve;a, khi doanh số của cả Apple lẫn ch&iacute;nh Samsung đều bị chững lại.</em></p>', 28990000, 18990000, 10, '2019-12-29 01:13:03', '2019-12-29 01:13:03'),
(18, 'Samsung Galaxy Fold', 1, 2, '<h2><a href=\"https://cellphones.com.vn/dien-thoai-samsung-galaxy-fold.html\" target=\"_blank\"><strong>Galaxy Fold</strong></a><strong>: Khởi nguồn kỷ nguy&ecirc;n điện thoại m&agrave;n h&igrave;nh gập</strong></h2>\r\n\r\n<p><em><strong>Samsung Galaxy Fold</strong>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">điện thoại</a>&nbsp;đầu ti&ecirc;n tr&ecirc;n thế giới được b&aacute;n ra với thiết kế m&agrave;n h&igrave;nh gập c&ugrave;ng nhiều t&iacute;nh năng hấp dẫn. Ngo&agrave;i ra, smartphone c&ograve;n sở hữu bộ vi xử l&yacute; mạnh mẽ c&ugrave;ng dung lượng ram khủng l&ecirc;n tới 12GB v&agrave; nhiều trang bị nổi trội kh&aacute;c.</em></p>', 50000000, 0, 10, '2019-12-29 01:14:02', '2019-12-29 01:14:02'),
(19, 'Samsung Galaxy A30s', 1, 2, '<h2><a href=\"https://cellphones.com.vn/samsung-galaxy-a30s.html\" target=\"_blank\"><strong>Điện thoại Samsung Galaxy A30s</strong></a><strong>&nbsp;&ndash; M&agrave;n h&igrave;nh&nbsp;</strong><strong>Infinity-U</strong><strong>, bộ ba camera g&oacute;c r</strong><strong>ộng</strong></h2>\r\n\r\n<p>Samsung vừa cho ra mắt điện thoại&nbsp;<strong>Samsung A30s</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh Infinity-U l&agrave; kiểu m&agrave;n h&igrave;nh giọt nước độc đ&aacute;o cho bạn trải nghiệm tối ưu. Th&ecirc;m v&agrave;o đ&oacute;, thiết kế m&aacute;y sang trọng v&agrave; bộ 3 camera chụp ảnh g&oacute;c rộng ho&agrave;n hảo c&ograve;n mang lại những khung h&igrave;nh sắc n&eacute;t cho người d&ugrave;ng.&nbsp;Ngo&agrave;i ra,&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a>&nbsp;cũng ra phi&ecirc;n bản n&acirc;ng cấp kh&aacute;c như&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a50s.html\" target=\"_blank\">Samsung A50s</a>,&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a10s.html\" target=\"_blank\">Samsung A10s</a>,&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a20s.html\" target=\"_blank\">Samsung A20s</a>,&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a70s.html\" target=\"_blank\">Samsung A70s</a></p>', 6290000, 4850000, 40, '2019-12-29 01:17:07', '2019-12-29 01:17:07'),
(20, 'Samsung Galaxy S9+ (Plus) 64GB', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s9plus-chinh-hang.html\" target=\"_blank\">Điện thoại Samsung Galaxy S9 Plus 64GB</a>&nbsp;-&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">Smartphone</a>&nbsp;thiết kế sang trọng v&agrave; nhiều n&acirc;ng cấp vượt trội</strong></h2>\r\n\r\n<p><em>Sam Fan đ&atilde; được diện kiến&nbsp;<strong>Samsung Galaxy S9 Plus</strong>&nbsp;v&agrave;o th&aacute;ng 2/2018 v&agrave; g&acirc;y ấn tượng mạnh mẽ. Thiết kế lẫn hiệu năng của S9 Plus đ&atilde; chứng tỏ đ&acirc;y xứng đ&aacute;ng l&agrave; một trong những smartphone si&ecirc;u phẩm tr&ecirc;n thế giới. Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s10-lite.html\" target=\"_blank\">Samsung S10 Lite</a>&nbsp;- bản r&uacute;t gọn với gi&aacute; phải chăng hơn của Galaxy S10 v&agrave; S10+ sắp được ra mắt.</em></p>', 19990000, 13090000, 20, '2019-12-29 01:18:42', '2019-12-29 01:18:42'),
(21, 'Samsung Galaxy A20s', 1, 2, '<h2><a href=\"https://cellphones.com.vn/samsung-galaxy-a20s.html\" target=\"_blank\">Điện thoại Samsung Galaxy A20s&nbsp;</a>&ndash; Th&ecirc;m một sự lựa chọn đến từ chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">smartphone&nbsp;</a>m&agrave;n h&igrave;nh nhỏ hơn</h2>\r\n\r\n<p><em><strong>Samsung Galaxy A20s</strong>&nbsp;l&agrave; phi&ecirc;n bản kế nhiệm&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a20.html\" target=\"_blank\">Galaxy A20</a>&nbsp;vừa được Samsung cho ra mắt. Galaxy A20s được dự đo&aacute;n sử dụng chip Exynos 7884, cụm camera k&eacute;p được n&acirc;ng cấp cũng như m&agrave;n h&igrave;nh giọt nước. Đ&acirc;y cũng l&agrave; mẫu smartphone được kỳ vọng sẽ gi&uacute;p Samsung tăng thị phần trong ph&acirc;n kh&uacute;c smartphone tầm trung</em></p>', 4390000, 0, 20, '2019-12-29 01:20:17', '2019-12-29 01:20:17'),
(22, 'Samsung Galaxy Tab A8 2019 (T295)', 4, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-tab-a-t295.html\" target=\"_blank\">M&aacute;y t&iacute;nh bảng Samsung Galaxy Tab A8 (T295) 2019</a>&nbsp;&ndash; Chiếc tablet gi&aacute; rẻ mạnh mẽ của Samsung năm 2019</strong></h2>\r\n\r\n<p><em>C&ugrave;ng với điện thoại th&ocirc;ng minh, c&aacute;c d&ograve;ng&nbsp;<a href=\"https://cellphones.com.vn/tablet/samsung.html\" target=\"_blank\"><strong>m&aacute;y t&iacute;nh bảng đến từ Samsung</strong></a>&nbsp;ng&agrave;y c&agrave;ng nhận được sự t&iacute;n nhiệm của người ti&ecirc;u d&ugrave;ng. Sau th&agrave;nh c&ocirc;ng của Samsung Galaxy Tab A8 2019, h&atilde;ng c&ocirc;ng nghệ H&agrave;n Quốc tiếp tục giới thiệu với thị trường c&ocirc;ng nghệ &ldquo;si&ecirc;u phẩm gi&aacute; rẻ&rdquo; mới -&nbsp;<strong>Samsung Galaxy Tab&nbsp;<em>A8 (T295) 2019</em></strong>. Chiếc&nbsp;<a href=\"https://cellphones.com.vn/tablet.html\" target=\"_blank\"><strong>m&aacute;y t&iacute;nh bảng</strong></a>&nbsp;n&agrave;y hứa hẹn sẽ mang đến cho người d&ugrave;ng những trải nghiệm tuyệt vời so với những g&igrave; tầm gi&aacute; phổ th&ocirc;ng c&oacute; thể mang lại.</em></p>', 3690000, 3450000, 10, '2019-12-29 01:20:53', '2019-12-29 01:20:53'),
(23, 'Samsung Galaxy A50 128GB', 1, 2, '<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-a50.html\" target=\"_blank\">Điện thoại Samsung Galaxy A50</a>&nbsp;&ndash;&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">Smartphone</a>&nbsp;với m&agrave;n h&igrave;nh tr&agrave;n viền, cụm 3 camera ấn tượng</strong></h2>\r\n\r\n<p><em>Sau&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a20.html\" target=\"_blank\"><strong>Galaxy A20</strong></a>&nbsp;v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a30-64gb.html\" target=\"_blank\"><strong>Galaxy A30</strong></a>&nbsp;th&igrave; mới đ&acirc;y&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\"><strong>SamSung</strong></a>&nbsp;tiếp tục bổ sung một c&aacute;i t&ecirc;n mới v&agrave;o danh s&aacute;ch&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-a.html\" target=\"_blank\"><strong>Galaxy A</strong></a>&nbsp;của m&igrave;nh, đ&oacute; ch&iacute;nh l&agrave;&nbsp;<strong>SamSung Galaxy A50</strong>. A50 với nhiều c&ocirc;ng nghệ với v&agrave; hấp dẫn như cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, cụm 3 camera hứa hẹn sẽ mang đến trải nghiệm tuyệt vời cho người d&ugrave;ng. Cuối năm 2019, Samsung cũng đ&atilde; ra mắt mẫu điện thoại kế tiếp với t&ecirc;n gọi&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a50s.html\" target=\"_blank\">Samsung Galaxy A50s</a></em></p>', 5600000, 0, 20, '2019-12-29 01:23:01', '2019-12-29 01:23:01'),
(24, 'Samsung Galaxy A9 (2018)', 1, 2, '<h2>Samsung Galaxy A9 2018 - Cấu h&igrave;nh v&agrave; gi&aacute; b&aacute;n</h2>\r\n\r\n<p>Cuộc đua tr&ecirc;n thị trường c&ocirc;ng nghệ đang diễn ra v&ocirc; c&ugrave;ng khốc liệt khi c&aacute;c nh&agrave; sản xuất thay phi&ecirc;n nhau thiết lập n&ecirc;n những cột mốc mới, chẳng hạn như c&aacute;ch m&agrave; h&atilde;ng&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">điện thoại Samsung</a>&nbsp;trang bị đến 4 camera sau cho&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a9-2018-chinh-hang.html\" target=\"_blank\"><strong>Samsung&nbsp;Galaxy A9 2018</strong></a></p>', 12490000, 8250000, 10, '2019-12-29 01:24:48', '2019-12-29 01:24:48'),
(25, 'Xiaomi Redmi Note 8', 1, 3, '<h2><a href=\"https://cellphones.com.vn/xiaomi-redmi-note-8.html\" target=\"_blank\">Điện thoại Xiaomi Redmi Note 8</a>&nbsp;&ndash; Chiếc điện thoại Xiaomi với phần cứng mạnh mẽ</h2>\r\n\r\n<p><strong><em>(*)Điện thoại Xiaomi ch&iacute;nh h&atilde;ng, c&oacute; sẵn tiếng Việt, đầy đủ ứng dụng của Google. Bạn c&oacute; thể sử dụng ngay b&igrave;nh thường. Kh&aacute;c với c&aacute;c m&aacute;y h&agrave;ng x&aacute;ch tay: kh&ocirc;ng c&oacute; sẵn rom tiếng Việt, chặn c&aacute;c ứng dụng của Google</em></strong></p>\r\n\r\n<p><em><strong>Redmi Note 8</strong>&nbsp;l&agrave; chiếc smartphone tầm trung mới nhất của Xiaomi bẹn cạnh mẫu điện thoại&nbsp;<a href=\"https://cellphones.com.vn/xiaomi-redmi-note-8-pro.html\" target=\"_blank\">Redmi Note 8 Pro</a>.&nbsp;<em>Xiaomi Redmi Note 8</em>&nbsp;g&acirc;y ấn tượng với cấu h&igrave;nh phần cứng mạnh mẽ, hệ thống bốn camera sau chất lượng v&agrave; đi k&egrave;m gi&aacute; b&aacute;n cực kỳ hấp dẫn.&nbsp;<strong>Xiaomi Redmi Note 8</strong>&nbsp;sẽ l&agrave; chiếc điện thoại g&acirc;y nhiều ch&uacute; &yacute; nhất trong thời gian sắp tới.</em></p>', 4990000, 4200000, 20, '2019-12-29 01:30:26', '2019-12-29 01:30:26'),
(26, 'Xiaomi Mi 9 Lite', 1, 3, '<h2><strong><a href=\"https://cellphones.com.vn/xiaomi-mi-9-lite.html\" target=\"_blank\">Điện thoại Xiaomi Mi 9 lite 128GB</a>&nbsp;- chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile.html\" target=\"_blank\">điện thoại gi&aacute; rẻ</a>&nbsp;với thiết kế sang trọng c&ugrave;ng nhiều t&iacute;nh năng cao cấp</strong></h2>\r\n\r\n<p>Xiaomi đ&atilde; l&agrave; một trong những c&aacute;i t&ecirc;n qu&aacute; l&agrave; quen thuộc đối với đại đa số người d&ugrave;ng ng&agrave;y nay, những sản phẩm smartphone của họ đều c&oacute; chất lượng tốt, thiết kế đẹp c&ugrave;ng nhiều t&iacute;nh năng th&uacute; vị. V&agrave; chiếc<strong>&nbsp;Xiaomi Mi 9 Lite 128GB</strong>&nbsp;ch&iacute;nh l&agrave; minh chứng cho điều ấy.</p>', 6690000, 5890000, 20, '2019-12-29 01:36:48', '2019-12-29 01:36:48'),
